  // check the address is a NFT Token Holder
  function isAllowedInTier0(address _address) public view returns(bool) {
    
    IERC1155 token = IERC1155(zsalesPlatformDetails.zsalesNFTTokenAddress);
    address[] memory addresses=new address[](zsalesPlatformDetails.maxTiersToCheckForNFT);
    addresses[0]=_address;

    uint[] memory tokenIds=new uint[](zsalesPlatformDetails.maxTiersToCheckForNFT);
    
    for (uint256 i = 0; i < zsalesPlatformDetails.maxTiersToCheckForNFT; i++) {
        tokenIds[i]=i;
    }

    uint[] memory balances = token.balanceOfBatch(addresses,tokenIds) ;
    uint balance = 0;
    for (uint256 i = 0; i < zsalesPlatformDetails.maxTiersToCheckForNFT; i++) {
        balance += balances[i];
    }
    return balance>0;
  }


